import React, { Fragment } from 'react';
import { Component } from 'react';
import './App.css';
import { NavLink, Switch, Route } from 'react-router-dom';
import mainLogo from'./image/tovit_logo.svg';
import FadeIn from "react-fade-in";
import axios from 'axios'


  const App = () => (
    <div className='app'>
      <h1>Tovit</h1>
      <Navigation />
      <Main />
    </div>
  );


  const Navigation = () => (
    <nav>
      <ul>
        <li><NavLink exact activeClassName="current" to='/Dashboard'>Dashboard</NavLink></li>
        <li><NavLink exact activeClassName="current" to='/ContactTracing'>Contact Tracing</NavLink></li>
        <li><NavLink exact activeClassName="current" to='/Supplies'>Supplies</NavLink></li>
        <li><NavLink exact activeClassName="current" to='/Chatbot'>Chatbot</NavLink></li>
      </ul>
    </nav>
  );

  const Main = () => (
    <Switch>
      {/* <Route path='/' component={Home}></Route> */}
      <Route exact path='/Dashboard' component={Dashboard}></Route>
      <Route exact path='/ContactTracing' component={ContactTracing}></Route>
      <Route exact path='/Supplies' component={Supplies}></Route>
      <Route exact path='/Chatbot' component={Chatbot}></Route>
    </Switch>
  );

  // const Home = () => (
  //   <FadeIn>
  //     <div className="root">
  //         <img src={mainLogo}/>
  //     </div>
  //   </FadeIn>
  // );

  const Dashboard = () =>(
    renderHTML("\
        <div class=\"container-fluid\">\
          <div class=\"row mb-1\">\
            <div class=\"col-sm\">\
              <nav class=\"navbar navbar-expand-sm navbar-dark bg-dark\">\
                <div class=\"navbar-brand mb-0 h1\">\
                  <i class=\"fas fa-chart-bar\"></i> <a class=\"navbar-brand\" href=\"https://trekhleb.github.io/covid-19/\">COVID-19 Dashboard</a>\
                </div>\
                <button class=\"navbar-toggler\" type=\"button\" data-toggle=\"collapse\" data-target=\"#navbarNav\" aria-controls=\"navbarNav\" aria-expanded=\"false\" aria-label=\"Toggle navigation\">\
                  <span class=\"navbar-toggler-icon\"></span>\
                </button>\
                <div class=\"collapse navbar-collapse\" id=\"navbarNav\">\
                </div>\
              </nav>\
            </div>\
          </div>\
          <div class=\"row mb-1\">\
            <div class=\"col-sm\">\
              <div id=\"root\"></div>\
            </div>\
          </div>\
          <div class=\"row mb-4\">\
            <div class=\"col-sm\">\
              <small class=\"text-secondary text-nowrap mr-3\">\
                <i class=\"fas fa-table\"></i> Data source: <a href=\"https://github.com/CSSEGISandData/COVID-19\" class=\"text-dark\">COVID-19 Data Repository by JHU CSSE</a>\
              </small>\
              <small class=\"text-secondary text-nowrap mr-3\">\
                <i class=\"fas fa-bug\"></i> <a href=\"https://github.com/trekhleb/covid-19/issues\" class=\"text-dark\">Report issue</a>\
              </small>\
              <small class=\"text-secondary text-nowrap mr-3\">\
                <i class=\"fab fa-github\"></i> <a href=\"https://github.com/trekhleb/covid-19/\" class=\"text-dark\">Contribute</a>\
              </small>\
            </div>\
          </div>\
        </div>\
        <script src=\"https://unpkg.com/react@16/umd/react.development.js\" crossorigin></script>\
        <script src=\"https://unpkg.com/react-dom@16/umd/react-dom.development.js\" crossorigin></script>\
        <script\
          src=\"https://ajax.googleapis.com/ajax/libs/jquery/3.4.1/jquery.min.js\"\
          crossorigin=\"anonymous\"\
        ></script>\
        <script\
          src=\"https://cdn.jsdelivr.net/npm/popper.js@1.16.0/dist/umd/popper.min.js\"\
          integrity=\"sha384-Q6E9RHvbIyZFJoft+2mJbHaEWldlvI9IOYy5n3zV9zzTtmI3UksdQRVvoxMfooAo\"\
          crossorigin=\"anonymous\"\
        ></script>\
        <script\
          src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.4.1/js/bootstrap.min.js\"\
          integrity=\"sha384-wfSDF2E50Y2D1uUdj0O3uMBJnjuUD4Ih7YwaYd1iqfktj0Uod8GCExl3Og8ifwB6\"\
          crossorigin=\"anonymous\"\
        ></script>\
        <script src=\"https://unpkg.com/papaparse@5.1.1/papaparse.min.js\" crossorigin></script>\
        <script src=\"https://cdnjs.cloudflare.com/ajax/libs/Chart.js/2.9.3/Chart.min.js\" crossorigin></script>\
        <script src=\"js/utils.js\"></script>\
        <script src=\"js/data.js\"></script>\
        <script src=\"js/index.js\"></script>\
      </body>\
      </html>\
    ")
    );


  const ContactTracing = () => {


  }; 

  const Supplies = () => {
     state = {
      venues: []
    }

    componentDidMount(); {
      this.getVenues()
    }

    renderMap = () => {
      loadScript("https://maps.googleapis.com/maps/api/js?key=AIzaSyDchFq6AuwSdBcSMNJlstC7aQK31cuN1lc&callback=initMap")
      window.initMap = this.initMap
    }

    getVenues = () => {
      const endPoint = "https://api.foursquare.com/v2/venues/explore?"
      const parameters = {
        client_id: "PMHC2WA1VCBHVYOPPSJ0QSBYTLRF4PNJ04OWVWV0PZJ0QFIR",
        client_secret: "CULSZZ44YAEBOWBFGPB4BF5ISRXXSNYR0EE3JV3CNE2ZWHV0",
        query: "groceries",
        near: "Dubai",
        v: "20182507"
      }

      axios.get(endPoint + new URLSearchParams(parameters))
        .then(response => {
          this.setState({
            venues: response.data.response.groups[0].items
          }, this.renderMap())
        })
        .catch(error => {
          console.log("ERROR!! " + error)
        })

    }

    initMap = () => {

      // Create A Map
      var map = new window.google.maps.Map(document.getElementById('map'), {
        center: {lat: 25.0769, lng: 55.1341},
        zoom: 8
      })

      // Create An InfoWindow
      var infowindow = new window.google.maps.InfoWindow()

      // Display Dynamic Markers
      this.state.venues.map(myVenue => {

        var contentString = `${myVenue.venue.name}`

        // Create A Marker
        var marker = new window.google.maps.Marker({
          position: {lat: myVenue.venue.location.lat , lng: myVenue.venue.location.lng},
          map: map,
          title: myVenue.venue.name
        })

        // Click on A Marker!
        marker.addListener('click', function() {

          // Change the content
          infowindow.setContent(contentString)

          // Open An InfoWindow
          infowindow.open(map, marker)
        })

      })

    }


    render(); {
      return (
        <main>
          <div id="map"></div>
        </main>
      )
    }

    function loadScript(url) {
      var index  = window.document.getElementsByTagName("script")[0]
      var script = window.document.createElement("script")
      script.src = url
      script.async = true
      script.defer = true
      index.parentNode.insertBefore(script, index)
    }








    };

  const Chatbot = () => {
    const demoWatson = () => {
      window.loadWatsonAssistantChat({
        integrationID: "5bd1bb25-2d16-40f8-abb3-fa92f8829d63", // The ID of this integration.
        region: "eu-gb", // The region your integration is hosted in.
        serviceInstanceID: "a513e694-d417-4035-a972-26d6d679c99c" // The ID of your service instance.
        // onLoad: function(instance) { instance.render(); }
      }).then(function(instance){
        instance.render();
      }
      );
    }
    demoWatson()
    // <div className='Supplies'>
    //   <h1>About Me</h1>
    //   <p>Ipsum dolor dolorem consectetur est velit fugiat. Dolorem provident corporis fuga saepe distinctio ipsam? Et quos harum excepturi dolorum molestias?</p>
    //   <p>Ipsum dolor dolorem consectetur est velit fugiat. Dolorem provident corporis fuga saepe distinctio ipsam? Et quos harum excepturi dolorum molestias?</p>
    // </div>
  };



export default App;
